insert into criteria(zone_id, section_id, description, max_value, coefficient)
values
((select id from zones where name = ''), (select id from sections where name = ''), 'Форма всех сотрудников соответствует стандарту. Сотрудники кухни (кассир при работе на упаковке) с бородой на всех станциях носят набородник', 2, default),
((select id from zones where name = ''), (select id from sections where name = ''), 'Менеджер смены делает обходы.', 2, default),
((select id from zones where name = ''), (select id from sections where name = ''), 'Сотрудники не используют личные телефоны в зеленой зоне пиццерии, не носят их в карманах', 2, default),
((select id from zones where name='Потери'), (select id from sections where name=''), 'Продукт готовится по стандарту. Соблюдаются стандарты начинения, приготовления продукта, упаковки.  Нет перерасхода ингредиентов. До 3-х ошибок, 4 и более - крит. фактор', 4, default),
((select id from zones where name='Потери'), (select id from sections where name=''),'При приемке товар взвешивается, пересчитывается. Товар на пол не ставится. Разбор поставки не более 1,5 часов (ВЗ), не более 1 часа - тесто',2, default),
((select id from zones where name='Потери'), (select id from sections where name=''), 'Продукты не размораживаются в ГЦ и ХЦ, мойке, на печи. Не лежат на столах более чем 30 минут. Одновременная заготовка не более 1 ингредиента одним сотрудником',4, default),
((select id from zones where name='Потери'), (select id from sections where name=''), 'На линиии начинения на каждый сыпучий ингредиент есть отдельный стаканчик. Используются актуальные стаканчики.',1, default),
((select id from zones where name='Курьеры'), (select id from sections where name=''), 'У всех сумок есть место для хранения ,не хранятся на полу.',1, default),
((select id from zones where name='Курьеры'), (select id from sections where name=''), 'Внешний вид курьеров соответствует стандарту.',1, default),
((select id from zones where name='Кассир'), (select id from sections where name=''), 'Ответственный сотрудник не оставляет открытым денежный ящик.',1 , default),
((select id from zones where name='Касса'), (select id from sections where name=''), 'Кассир дружелюбен, моментально реагирует на гостя. Принимает заказы быстро, помогает с выбором. Не игнорирует гостя, не стоит спиной к нему, в закрытой позе. Реакция на звонок не более 30 секунд.',3, default ),
((select id from zones where name='Касса'), (select id from sections where name=''), 'Заказы в ресторан выданы на подносе согласно стандарту комплектации заказов. Гостю выдан чек (РБ). Пицца на металлическом подносе выдана согласно стандарту.',2 , default),
((select id from zones where name='Тесто'), (select id from sections where name=''), 'Тесто хранится и используется по стандарту, на раскатке одновременно не более 6-и плюшек.', 2, default),
((select id from zones where name='Кухня'), (select id from sections where name=''), 'Линия полностью заполнена гастроемкостями - герметична. При отсутствии заказов линия и гастроемкости закрыты.',2, default ),
((select id from zones where name='Кухня'), (select id from sections where name=''), 'Условия хранения продуктов и расходников соответствует стандартам (температура, 15 см от пола, пищевое/непищевое). В желтой зоне нет вскрытых упаковок с напитками.', 4 , default),
((select id from zones where name='Кухня'), (select id from sections where name=''), 'Овощи моются по стандарту, необработанные овощи не кладут на столы в ГЦ и ХЦ.', 3, default ),
((select id from zones where name='Кухня'), (select id from sections where name=''), 'Пиццы готовятся согласно принципу конвейера.',2, default ),
((select id from zones where name='Ресторан'), (select id from sections where name=''), 'Зал: чистый пол, нет следов от ботинок, сильных загрязнений.',2, default ),
